/* 
Component React - Search

Использует Local State
 
*/

import React, {Component} from 'react'
import {connect} from 'react-redux'

import {searchPhone} from 'actions'

class Search extends Component {

    // Конструктор локального стейта
    constructor (props) {
        super(props)
        this.state = {
          value: ''
        }
    
        // Привязываем методы к this внутри конструктора
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
      }
    
      // Метод на изменение поля
      handleChange (event) {
        this.setState({
          value: event.target.value
        })
      }
    // Метод на отсылку формы
      handleSubmit(event) {
        event.preventDefault();
        this.props.searchPhone(this.state.value)
      }
    


// Отрисовка компонента - Обычная форма form/input/button
render () {
    return (
      <div className='well blosd'>
        <h3 className='lead'>Quick shop</h3>
        <div className='input-group'>
          <form onSubmit={this.handleSubmit}>
            <input
              onChange={this.handleChange}
              type='text'
              className='form-control'
            />
          </form>
          <span className='input-group-btn'>
          <button className='btn btn-default'>
            <span className='glyphicon glyphicon-search'/>
          </button>
        </span>
        </div>
      </div>
    )
  }
}

// Отправляем в props
const mapDispatchToProps = {
    searchPhone
  }
  
  export default connect(null, mapDispatchToProps)(Search)