/* 
  Layout - стандартная обертка вокруг любой страницы,
     содержащая в себе место для контента и сайтбар 
*/

import React from "react";

import Sidebar from 'components/sidebar';

// Layout получает дочерний объект и вставляет его в основное окно
const Layout = ({children}) => (
    <div className='view-container'>
        <div className='container'>
            <div className='row'>
                <div className='col-md-3'>
                < Sidebar/>
                </div>
                <div className='col-md-9'>
                    {children}
                </div>
            </div>
        </div>
    </div>
)

export default Layout;