/* 
  Phones - React ,
  обернутый в декоратор connect для связи с Store

  Если строка составная, то ее обертывают в кавычки:
  <Link to={`/phones/${phone.id}`}>
*/

import React, {Component} from 'react'
import {connect} from 'react-redux'
// Для использования тегов <Link></Link>
import {Link} from 'react-router'
import R from "ramda";

// Импортируем модули
import {fetchPhones} from 'actions'
import {getPhones} from 'selectors'

class Phones extends Component {
  componentDidMount () {
    this.props.fetchPhones() // Action приходит из props
  }

  renderPhone (phone, index) {
    // Метод выводит только первые 60 символов описания ( обернуто в строку ES-6)
    const shortDescription = `${R.take(60, phone.description)}...`
  
    return (
      <div className='col-sm-4 col-lg-4 col-md-4 book-list' key={index}>
        <div className='thumbnail'>
          <img
            className='img-thumbnail'
            src={phone.image}
            alt={phone.name}
          />
          <div className='caption'>
            <h4 className='pull-right'>${phone.price}</h4>
            <h4>
              <Link to={`/phones/${phone.id}`}>
                {phone.name}
              </Link>
            </h4>
            <p>{shortDescription}</p>
            <p className='itemButton'>
              <button
                className='btn btn-primary'
              >
                Buy Now!
              </button>
              <Link
                to={`/phones/${phone.id}`}
                className='btn btn-default'
              >
                More info
              </Link>
            </p>
          </div>
        </div>
      </div>
    )
  }
   
  render () {
    // Вызываем из props phones
    const {phones} = this.props
    return (
      <div>
      <div className='books row'>
        {phones.map((phone, index) => this.renderPhone(phone, index))}
      </div>
    </div>
    )
  }
}

// Получаем phones из state в props.phones
const mapStateToProps = state => ({

  phones: getPhones(state)
})

const mapDispatchToProps = {
  // Сокращенная форма записи dispatch и т.д.
  fetchPhones
}
// Обетрываем export в декоратор connect
export default connect(mapStateToProps, mapDispatchToProps)(Phones)