/* 
  Reducer который будет хранить только idшники телефонов, которые мы добавили в корзину.

  Reducer это функция, которая берет предыдущее состояние и action
 и возвращает новое состояние. Она является чистой,
 так как не модифицирует данные, а возвращает новые данные.

*/

import R from 'ramda'

import {
  ADD_PHONE_TO_BASKET
} from 'actionTypes'

// Первоначальное состояние state - это пустой массив 
const initialState = []


export default (state = initialState, {type, payload}) => {
  switch (type) {
    case ADD_PHONE_TO_BASKET:
    // Фукция добавляет payload в конец state массива, возвращает новый массив и данные не мутируют
      return R.append(payload, state)
    default:
      return state
  }
}