/* 
Reducer - Categories

почти такой же как и reducers/phones
*/

import R from 'ramda'

import {
  FETCH_CATEGORIES_SUCCESS
} from 'actionTypes'

const initialState = {}

export default (state = initialState, {type, payload}) => {
  switch (type) {
      // При удачной загрузке из API
    case FETCH_CATEGORIES_SUCCESS:
      const newValues = R.indexBy(R.prop('id'), payload)
      // Сливаем вместе state & newValues
      return R.merge(state, newValues)
    default:
      return state
  }
}

