/* 
Reducer - phonePage

Теперь давайте создадим редьюсер phonePage.
 В нем у нас будет initialState обьект с ключем id. 
 И соотвественно мы будем получать телефон из редьюсера phones по id
*/

import R from 'ramda'

import {
  FETCH_PHONE_BY_ID_SUCCESS
} from 'actionTypes'

const initialState = {
  id: null
}

export default (state = initialState, {type, payload}) => {
  switch (type) {
    case FETCH_PHONE_BY_ID_SUCCESS:
      return R.merge(state, {
        id: R.prop('id', payload)
      })
    default:
      return state
  }
}